div.script:
var all_labels = [];
var currentposturl="";
var maxresults=10;
all_labels.push("");

body.script:
        //
   	var maxrs = 12;
	var pageNo = 1;
	var obj;
    function find_url(target_url){
    	var curl ="";
    	if (target_url)
			curl = target_url;
		else{
			curl= window.location.href;
		}
		curl = curl.replace("?m=1","").replace("#","");
		var feed_url = '/feeds/posts/default/?max-results=' + maxrs + '&v=2&alt=json';
		var label = "";
		var pattern = /page\/(\d+)$/i
		var result = pattern.exec(curl);
		if (result) {
			pageNo = result[1];
		    feed_url = '/feeds/posts/default/?start-index=' + (pageNo-1) * maxrs + '&max-results='+maxrs+'&v=2&alt=json';
		}
		pattern = /search\/label\/(.+?)$/i
		result = pattern.exec(curl);
		if (result) {
			label = result[1];
		    feed_url = '/feeds/posts/default/-/' + label + '/?max-results='+maxrs+'&v=2&alt=json';
		}
		pattern = /search\/label\/(.+?)\/(\d+)$/i
		result = pattern.exec(curl);
		if (result) {
		    pageNo = result[2];
		    label = result[1];
		    feed_url = '/feeds/posts/default/-/' + label + '/?start-index=' + (pageNo - 1) * maxrs + '&max-results='+maxrs+'&v=2&alt=json';
		}
		return {"pageNo":pageNo,"label":label,"feed_url":feed_url};
    }

    function aj_dsp(target_url){
    	obj = find_url(target_url);
    	aj_dsp_post(obj);

    }
    function aj_dsp_post(obj) {
        $.ajax({
                url: obj.feed_url,
                contenType: 'application/json',
            })
            .done(function(data) {
                if (data.feed.entry){
                	var html = '';
                	var img_html = '';
                    for (var i in data.feed.entry) {
                        var e = data.feed.entry[i];
                        img_html =  '<img class="thumb mb-2" src="'+$(e.content.$t).find(".wp-post-image").attr("src").replace(".jpg","m.jpg")+'"/>';
                        html += '<div class="col-lg-2 col-sm-4 article mt-2  mb-2 ">' + img_html + '<p class="title "><a class="text-steel-blue font-weight-bold" href="' + e.link[4].href + '">' + e.title.$t + '</a></p>' + '</div>' ;

                    }
                    $(".mypost").append(html);
                    obj.pageCount = data["feed"]["openSearch$totalResults"]["$t"];

                }
                else{
                	 $(".btn_load_more").addClass("disabled").addClass("btn-outline-secondary").html("---- The End ----")
                }
            });
    }


    //related post
    var relObj = [],
        uniq_href = [],
        ri = 0;

    //related post
  $(document).ready(function() {
      if (!is_item_page) {
      	$(".btn_load_more").unbind().on("click",function(){
      			pageNo++;
				base_url = base_url.replace("?m=1","").replace("#","");
				if (obj && obj.label){
					target_url=base_url+"search/label/"+obj.label;
					if (pageNo>1)
						target_url=base_url+"search/label/"+obj.label+"/"+pageNo;
				}
				else{
					target_url=base_url;
					if (pageNo>1)
						target_url=base_url+"page/"+pageNo;
				}
				aj_dsp(target_url);
      	});
         aj_dsp(window.location.href);
      } else {
          rmidx = all_labels.indexOf("IT Ebooks");
          if (rmidx >= 0) all_labels.splice(rmidx, 1);
          for (var i in all_labels)
              $.ajax({
                  type: "GET",
                  url: "/feeds/posts/default/-/" + all_labels[i] + "?alt=json&v=2&max-results=10",
                  contenType: 'application/json'
              })
              .done(function(data) {
                  if (data.feed.entry){

                  	 for (var r = 0; r < data.feed.entry.length; r++)
			            for (var l = data.feed.entry[r], t = 0; t < l.link.length; t++) "alternate" == l.link[t].rel && -1 == uniq_href.indexOf(l.link[t].href) && l.link[t].href != currentposturl && (uniq_href.push(l.link[t].href), relObj.push({
			                t: l.link[t].title,
			                h: l.link[t].href,
			                i: /<img.+?src="(.+?)"/.exec(l.content.$t)[1]
			            }));
			        for (; ri < maxresults && relObj.length > 0;) {
			            var n = Math.floor((relObj.length - 1) * Math.random());
			            jQuery(".related_post").append('<li><a  href="' + relObj[n].h + '"><img src="' + relObj[n].i.replace(".jpg", "t.jpg") + '"/><a/><a class="text-steel-blue font-weight-bold" href="' + relObj[n].h + '">' + relObj[n].t + "</a></li>"), relObj.splice(n, 1), ri++
			        }

                  }
              });

      }
  });
